document.getElementById("barCircle").addEventListener('mousedown',function(e){
	dragging = true;
	posX = e.clientX - document.getElementById("barCircle").offsetLeft - 2;
	document.addEventListener('selectstart',function(e){
		if(dragging){
			e.preventDefault();
		};
	});
	window.getSelection().empty();
	document.addEventListener('mouseup',function(e){
		dragging = false;
	});
	document.addEventListener('mousemove',function(e){
		if(dragging && (e.clientX - posX) >= 0 && (e.clientX - posX) <= Math.round($("#progressBar").width())){
			$("#barDone").attr('style','width:' + ((e.clientX - posX) / Math.round($("#progressBar").width()) * 100).toFixed(2) + '%');
			$("#barCircle").attr('style','left: calc(' + ((e.clientX - posX) / Math.round($("#progressBar").width()) * 100).toFixed(2) + '% - 3px)');
			$("#barDo").attr('style','width:' + (100 - ((e.clientX - posX) / Math.round($("#progressBar").width()) * 100)).toFixed(2) + '%');
			ap.seek((ap.audio.duration * (e.clientX - posX) / Math.round($("#progressBar").width())).toFixed(6));
		};
	});
});
document.getElementById("barCircle").addEventListener('touchstart',function(e){
	dragging = true;
	document.getElementById("phone-body").style.overflowX = 'hidden';
	posX = e.touches[0].clientX - document.getElementById("barCircle").offsetLeft - 2;
	document.addEventListener('selectstart',function(e){
		if(dragging){
			e.preventDefault();
		};
	});
	window.getSelection().empty();
	document.addEventListener('touchend',function(e){
		if(dragging){
			document.getElementById("phone-body").style.overflowX = 'scroll';
		};
		dragging = false;
	});
	document.addEventListener('touchmove',function(e){
		if(dragging && (e.touches[0].clientX - posX) >= 0 && (e.touches[0].clientX - posX) <= Math.round($("#progressBar").width())){
			$("#barDone").attr('style','width:' + ((e.touches[0].clientX - posX) / Math.round($("#progressBar").width()) * 100).toFixed(2) + '%');
			$("#barCircle").attr('style','left: calc(' + ((e.touches[0].clientX - posX) / Math.round($("#progressBar").width()) * 100).toFixed(2) + '% - 3px)');
			$("#barDo").attr('style','width:' + (100 - ((e.touches[0].clientX - posX) / Math.round($("#progressBar").width()) * 100)).toFixed(2) + '%');
			ap.seek((ap.audio.duration * (e.touches[0].clientX - posX) / Math.round($("#progressBar").width())).toFixed(6));
		};
	});
});
$("#barDone").click(function(e){
	ap.seek((ap.audio.currentTime * e.offsetX / this.offsetWidth).toFixed(6));
});
$("#barDo").click(function(e){
	ap.seek(((ap.audio.duration - ap.audio.currentTime) * e.offsetX / this.offsetWidth + ap.audio.currentTime).toFixed(6));
});
ap.on('timeupdate', () => {
	ap.lrc.update(ap.audio.currentTime + offset);
	$('#barDone').attr('style','width: ' + ((ap.audio.currentTime / ap.audio.duration) * 100).toFixed(2) + '%');
	$('#barCircle').attr('style','left: calc(' + ((ap.audio.currentTime / ap.audio.duration) * 100).toFixed(2) + '% - 3px)');
	$('#barDo').attr('style','width: ' + ((1 - (ap.audio.currentTime / ap.audio.duration)) * 100).toFixed(2) + '%');
	document.getElementById("timeDone").innerHTML = timeString(Math.floor(ap.audio.currentTime));
	$('#aplayer-lrc-test').attr('style','width:' + $(".aplayer-lrc-current").width() + 'px');
});
